mapper.update:
UPDATE port_status
id = #{portStatus.id,jdbcType=VARCHAR}, name = #{portStatus.name,jdbcType=VARCHAR}, port_id = #{portStatus.portId,jdbcType=INTEGER}, up_time = #{portStatus.upTime,jdbcType=VARCHAR}, speed = #{portStatus.speed,jdbcType=INTEGER}, time_stamp = #{portStatus.timeStamp,jdbcType=TIMESTAMP}, in_data = #{portStatus.inData,jdbcType=BIGINT}, out_data = #{portStatus.outData,jdbcType=BIGINT}, des = #{portStatus.des,jdbcType=VARCHAR}, status = #{portStatus.status,jdbcType=INTEGER}
WHERE switch_port = #{portStatus.switchPort,jdbcType=VARCHAR}

UPDATE port_status
cvlan = #{portStatus.cvlan,jdbcType=INTEGER}, pvlan = #{portStatus.pvlan,jdbcType=INTEGER},
WHERE id = #{portStatus.id,jdbcType=VARCHAR}

UPDATE port_status
SET
