mapper.select:
SELECT port_status.switch_port
FROM switches_list
         INNER JOIN port_status ON switches_list.id = port_status.switch_id
WHERE switches_list.ip = #{ip,jdbcType=VARCHAR}
  AND port_status.`name` = #{port,jdbcType=VARCHAR}
LIMIT 1
