     switch_id= #{switchesReachableHistory.switchId,jdbcType=INTEGER},
     reachable= #{switchesReachableHistory.reachable,jdbcType=INTEGER},
     down_time= #{switchesReachableHistory.downTime,jdbcType=TIMESTAMP},
     time_stamp= #{switchesReachableHistory.timeStamp,jdbcType=TIMESTAMP}

WHERE id = #{switchesReachableHistory.id,jdbcType=VARCHAR}
