mapper.update:
UPDATE wechat_user
no = #{wechatUser.no,jdbcType=VARCHAR}, username = #{wechatUser.username,jdbcType=VARCHAR}, open_id = #{wechatUser.openId,jdbcType=VARCHAR}, session_key = #{wechatUser.sessionKey,jdbcType=VARCHAR}, union_id = #{wechatUser.unionId,jdbcType=VARCHAR}, token = #{wechatUser.token,jdbcType=VARCHAR}, permission = #{wechatUser.permission,jdbcType=INTEGER}, group_name = #{wechatUser.groupName,jdbcType=VARCHAR}
WHERE no = #{wechatUser.no,jdbcType=VARCHAR}
